import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { Link } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useContext(AuthContext);

  const handleSubmit = (e) => {
    e.preventDefault();
    login(email, password);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 animate-fadeIn p-4">
      <div className="card-amazon bg-white bg-opacity-90 backdrop-blur-md rounded-xl shadow-2xl p-8 w-full max-w-md">
        <h1 className="text-4xl font-extrabold text-amazon text-center mb-8 animate-gradient-x tracking-tight">
          Smart Attendance System
        </h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-lg font-semibold text-amazon mb-2">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 ease-in-out"
              required
            />
          </div>
          <div>
            <label className="block text-lg font-semibold text-amazon mb-2">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 ease-in-out"
              required
            />
          </div>
          <button
            type="submit"
            className="btn-amazon w-full bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-3 rounded-lg font-semibold hover:from-blue-700 hover:to-indigo-700 focus:ring-4 focus:ring-blue-300 transition-all duration-300 ease-in-out transform hover:scale-105 active:scale-95"
          >
            Login
          </button>
        </form>
        <div className="mt-6 text-center text-gray-600">
          <span>Don't have an account? </span>
          <Link
            to="/register"
            className="text-blue-600 font-medium underline hover:text-blue-800 transition-colors duration-200"
          >
            Register
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Login;
